package mypack;

public class arythmicoperators {
	// Arithmetic operators +,-,*,/,%
	public static void main(String[] args) {
		// TODO Auto-generated method stub


		// declared variables
		//additional operators
		int a = 12 ,  b = 5;
		int c1 = a+b;
		System.out.println(c1=a+b);
		// Subtraction operators 
		int c2 =a-b ;
		System.out.println( c2=a-b);
		//Multiplication operator 
		int C3 =a*b;
		System.out.println(C3=a*b);
		//division operator
		int C4 =a/b;
		System.out.println(C4=a/b);
		//module operator
		//module % operator is used to find reminder in division//
		int C5 =a%b;
		System.out.println(C5= a%b );
		// the answer for  should come C4= a/b = 2.4but its coming only 2 because the dated is int if we change it to double than its comes in decimal//
		double C6= a/b ;
		System.out.println(C6=a/b);

	}
}
